import { default as Tensor } from './tensor';
import { default as Function } from './function';
/**
 * This class CausalNetCore provide access to core function and core tensor
 *
 * @class CausalNetCore
 * @example
 * [EXAMPLE ../examples/core.babel.js]
 */
class CausalNetCore{
    constructor(){
        this.F = new Function();
        this.T = new Tensor();
    }
    get CoreFunction(){
        return this.F.CoreFunction; 
    }

    get CoreTensor(){
        return this.T.CoreTensor; 
    }
}
export default new CausalNetCore();

/**
 * class Function is the primitive class for composing higher class.
 * This class wrap [Ramda](https://ramdajs.com/) for providing Functional programming style method.
 * @class Function
 */
class Function{
    constructor(){
        /**
         * @private { Object } Ramda instance
         */
        this.R = require('ramda');

        /**
         * @private { Logger } logger   
         */        
        this.logger = console;
    }
    /**
     * This method return core Ramda instance
     * @readonly
     * @memberof Function
     */
    get CoreFunction(){
        return this.R;
    }

    /**
     *
     * @param { Array } keys
     * @param { Object } item
     * @param { Any } defaultValue - return value in case of not found
     * @returns { Any } return found value or defaultValue
     * @memberof Function
     */
    getIn(keys, item, defaultValue){
        return R.pathOr(defaultValue, keys, item);
    }

    /**
     * set value into object by the keys list
     * @param { Array } keys
     * @param { Object } value
     * @param { Object } item - item to set
     * @returns { Any } return found value or defaultValue
     * @memberof Function
     */
    setIn(keys, value, item){
        return this.R.assocPath(keys, value, item);
    }

    /**
     * zip two array
     * @param { Array } pairs - two arrays to zip
     * @returns { Array } array after zip
     * @memberof Function
     */
    zip(...pairs){
        return this.R.zip(...pairs);
    }
    /**
     * Unzip array of pairs to two arrays
     * @param { Array } pairs - array of pairs to unzip
     * @returns { [Array1, Array2] } - two arrays 
     * @memberof Function
     */
    unzip(pairs){
        const R = this.R;
        const Unzip = (uz, [v1, v2])=>{
            uz[0] = [...uz[0], v1];
            uz[1] = [...uz[1], v2];
            return uz;
        };
        return R.reduce(Unzip,[[], []],pairs);
    }
}

export default Function;

export { default as Tensor } from './tensor';
export { default as Function } from './function';
export { default as Store } from './store';
export { default as causalNetCore } from './causalNetCore';

/**
 * class Store is the primitive class for composing higher store class.
 * This class wrap [levelup](https://www.tensorflow.org/js) 
 * for connect with abstract-leveldown compliant stores.
 * @class Store
 */
export default class Store{
    constructor(){
        this.levelup = require('levelup');
        this.store = null;
        this.logger = console;
    }

    async connect(){
        throw Error('implement required');
    }

    /**
     * Get item given the key
     * @param { String } key - key name
     * @param {boolean} [asBuffer=false] - return object or buffer array, default object
     * @returns { Object } item object 
     * @memberof MemoryCache
     */
    async getItem(key, asBuffer=false){
        if(!this.store){
            throw Error('store is not connect');
        }
        return new Promise((resolve, reject)=>{
            this.store.get(key, {asBuffer}, (err, data)=>{
                if(err){
                    this.logger.debug({key, err});
                    reject('error read');
                }
                else{
                    resolve({[key]: data});
                }
            });
        });
    }
    /**
     * Set new item with the given key and value
     * @param { String } key - key name of item
     * @param { Object } data - value of item
     * @returns { Promise } - new key promise
     * @memberof MemoryCache
     */
    async setItem(key, data){
        if(!this.store){
            throw Error('store is not connect');
        }
        return new Promise((resolve, reject)=>{
            this.store.put(key, data, (err)=>{
                if(err){
                    this.logger.debug({key, err});
                    reject('error write');
                }
                else{
                    resolve({[key]: data});
                }
            });
        });
    }
    /**
     * Delete item given the item key
     * @param { String } key - key name to delete
     * @returns { Promise } deleted key promise
     * @memberof MemoryCache
     */
    async delItem(key){
        if(!this.store){
            throw Error('store is not connect');
        }
        return new Promise((resolve, reject)=>{
            this.store.del(key, (err)=>{
                if(err){
                    this.logger.debug({err});
                    reject('error write');
                }
                else{
                    resolve(key);
                }
            });
        });
    }
    /**
     * Make name maching function based on prefix path
     * @param { String } name - prefix name
     * @returns { Function } name matching function
     * @memberof MemoryCache
     */
    makeCheckItemNameFn(name){
        var pattern = name;
        var regex = new RegExp(`${pattern}.*`,'g');
        return (fileName)=>fileName.match(regex) || [];
    }

    /**
     * Get the current items under the prefix path
     * @param { String } [namePath='/']
     * @returns { Array } list of item names
     * @memberof MemoryCache
     */
    async getItemList(namePath='/'){
        if(!this.store){
            throw Error('store is not connect');
        }
        const NameTester = this.makeCheckItemNameFn(namePath);
        return new Promise((resolve, reject)=>{
            let fileList = [];
            this.store.createKeyStream()
                .on('data', (key) =>{
                    key = key.toString('utf8');
                    if(NameTester(key).length===1){
                        fileList.push(key);
                    }
                })
                .on('close', () => resolve(fileList) )
                .on('end',  () => resolve(fileList) )
                .on('error', (err) =>{ 
                    this.logger.debug({ err });
                    reject('error getFileList') ;
                });
        });
    }
}

/**
 * class Tensor is the primitive class for composing higher class.
 * This class wrap [tensorflowjs](https://www.tensorflow.org/js) for providing tensor calculation
 * @class Tensor
 */
export default class Tensor{
    constructor(){
        this.T = require('@tensorflow/tfjs');
        require('@tensorflow/tfjs-node');
        require('@tensorflow/tfjs-node-gpu');
        this.logger = console;
    }
    /**
     * This method return the core tensor instance
     * @readonly
     * @memberof Tensor
     */
    get CoreTensor(){
        return this.T;
    }
}

import { default as BaseImgDataset } from './baseImgDataset';
import { Platform } from 'causal-net.utils';
import { LoggerMixins } from 'causal-net.log';
import { StorageMixins, indexDBStorage } from 'causal-net.storage';
import { MemCacheMixins, memDownCache } from 'causal-net.memcache';
import { default as Function } from './function';
import { default as ImageDatasetfetchMixins } from './imageDatasetfetch.mixins';
import { default as ImageDatasetPreprocessingMixins } from './imageDatasetPreprocessing.mixins';
import { default as ImageDatasetGeneratorMixins } from './imageDatasetGenerator.mixins';
import { PreprocessingMixins, imagePreprocessing } from 'causal-net.preprocessing';

export default class MnistDataset extends Platform.mixWith(BaseImgDataset, 
        [ ImageDatasetfetchMixins,
          ImageDatasetPreprocessingMixins,
          PreprocessingMixins,
          ImageDatasetGeneratorMixins,
          MemCacheMixins,
          StorageMixins,
          LoggerMixins ]){

    constructor(...args){
        super(...args);
        this.F = new Function();
        this.Storage = indexDBStorage;
        this.MemCache = memDownCache;
        const PreprocessingSample = async (masterSampleBuffer, sampleSize)=>{
            return await imagePreprocessing.imageSplit(masterSampleBuffer, sampleSize);
        };
        const PreprocessingLabel = async (masterLabelBuffer, labelSize)=>{
            return await imagePreprocessing.imageSplit(masterLabelBuffer, labelSize);
        };
        this.setPreprocessingFunctions(PreprocessingSample, PreprocessingLabel);
    }
};

export default class BaseImgDataset{
    constructor(datasetConfigure){
        this.configuration = datasetConfigure;
        const {numSamples, sampleSize, numClasses} = this.configuration;
        this.dataSize = [numSamples, ...sampleSize];

        this.numSamples = numSamples;
        this.sampleSize = sampleSize;
        this.numClass   = numClasses;
    }

    summary(){
        return this.configuration;
    }
}

export default class BaseNLPDataset{
    constructor(datasetConfigure){
        this.configuration = datasetConfigure;
        const {numSamples, sampleSize, numClasses} = this.configuration;
        this.dataSize = [numSamples, ...sampleSize];

        this.numSamples = numSamples;
        this.sampleSize = sampleSize;
        this.numClass   = numClasses;
    }

    summary(){
        return this.configuration;
    }
}

import { Function as BaseFunction} from 'causal-net.core';
import { Platform } from 'causal-net.utils';
import { default as FunctionMixins} from './function.mixins';
export default class Function extends Platform.mixWith(BaseFunction,[FunctionMixins]){
    constructor(){
        super();
    }
}

const FunctionMixins = (BaseFunctionClass)=> class extends BaseFunctionClass{ 
    getImgBufferSize(imgSize){
        const R = this.R;
        return R.reduce((s,d)=>s*d,1,imgSize);
    }
    generatorWithIndex(items){
        const R = this.R;
        return R.addIndex(R.map)((d, idx)=>[idx, d], items);
    }
    splitTrainTestSet(items, splitIndex){
        const R = this.R;
        return R.splitAt(splitIndex, items);
    }
};
export default FunctionMixins;

import { Sampling } from 'causal-net.utils';
const ImageDatasetfetchMixins = (BaseImageClass)=> class extends BaseImageClass{ 
    async fetchLabelChunk(saveDir, labelChunk){
        let labelChunkUrl = this.configuration.datasetUrl + labelChunk;
        let labelSavePath = saveDir + labelChunk;
        return await this.storage.fetchFile(labelChunkUrl, labelSavePath);
    }

    async fetchSampleChunk(saveDir, sampleChunk){
        let sampleChunkUrl = this.configuration.datasetUrl + sampleChunk;
        let sammpleSavePath = saveDir + sampleChunk;
        this.logger.debug({sampleChunkUrl, sammpleSavePath});
        return await this.storage.fetchPNGFile(sampleChunkUrl, sammpleSavePath);
    }

    selectChunks(numchunks, selectBy='random'){
        let chunkIdxs = Sampling.choice(this.configuration.chunkList, numchunks);
        return chunkIdxs.map((idx, ith)=>([`data-chunk-${idx}.png`,`label-chunk-${idx}`]));
    }

    async fetchDataset(saveDir='/mnist/',numchunks=10, selectBy='random'){
        let selectedChunks = this.selectChunks(numchunks, selectBy);
        this.savedChunks = [];
        for(let [sampleChunk, labelChunk] of selectedChunks){
            let samplePath = await this.fetchSampleChunk(saveDir, sampleChunk);
            let labelPath = await this.fetchLabelChunk(saveDir, labelChunk);
            this.savedChunks.push([samplePath, labelPath]);
            this.progress({saveChunks:[samplePath, labelPath]});
        };
        return this.F.unzip(this.savedChunks);
    }
};
export default ImageDatasetfetchMixins;

import { Sampling } from 'causal-net.utils';
const ImageDatasetGeneratorMixins = (BaseImageClass)=> class extends BaseImageClass{ 

    getTrainTestSet(trainSize=null){
        trainSize = trainSize||parseInt(this.savedPreprocessing.length*0.9);
        const [trainSet, testSet] = this.F.splitTrainTestSet(this.savedPreprocessing, trainSize);
        return [trainSet, testSet];
    }

    makeSampleGenerator(sampleIdxSet, batchSize=null, start=0, end=null){
        if(!this.savedPreprocessing){
            throw Error('preprocessing must be performed');
        }
        batchSize = batchSize?batchSize:sampleIdxSet.length;
        const _batches = this.F.hsplitEvery(sampleIdxSet, batchSize);
        const batches = Sampling.choice(_batches, _batches.length);
        if(end === null){
            end = batches.length;
        }
        let nextIndex = start, iterationCount = 0, step = 1;
        const batchGenerator = {
                next: async()=>{
                    let batchSamples = [], batchLabels = [];
                    for(let [samplePath, labelPath] of batches[nextIndex]){
                        let sampleItem = await this.preProcessingStorage.getItem(samplePath,true);
                        let labelItem = await this.preProcessingStorage.getItem(labelPath,true);
                        batchSamples = [...batchSamples, sampleItem[samplePath]];
                        batchLabels = [...batchLabels, labelItem[labelPath]];
                    }
                    nextIndex += step;
                    let idx = iterationCount;
                    iterationCount++;
                    return {idx, batchSize, data:[batchSamples, batchLabels]};
                },
                *[Symbol.iterator]() {
                    while(nextIndex < end){
                        yield this.next();
                    }
                }
            };
        return batchGenerator;
    }
};
export default ImageDatasetGeneratorMixins;

import { Stream } from 'causal-net.utils';
const ImageDatasetPreprocessingMixins = (BaseImageClass)=> class extends BaseImageClass{ 
    makePreprocessingStream(saveDir){
        const ImageBufferSize = this.F.getImgBufferSize(this.sampleSize);
        const LabelBufferSize = this.numClass;
        this.logger.debug({imageBufferSize: ImageBufferSize, 
                     labelBufferSize: LabelBufferSize});
        const TransformFn = (chunk, chunkEncoding, afterTransformFn) =>{
            const TransformAsync = async ()=>{
                let sampleBuffer = chunk.sample;
                let labelBuffer = chunk.label;
                this.logger.debug({sampleBuffer, labelBuffer});
                let preprocessedImgBuffer = await this.PreprocessingSampleFn(sampleBuffer, ImageBufferSize);
                let preprocessedLabelBuffer = await this.PreprocessingLabelFn(labelBuffer, LabelBufferSize);
                return { processedChunk: this.F.zip(preprocessedImgBuffer, preprocessedLabelBuffer), 
                         chunkIdx: chunk.idx};
            };
            TransformAsync(chunk).then(processedChunk=>{
                afterTransformFn(null, processedChunk);
            });
        };

        const WriteFn = (processedChunk, chunkEncoding, callback) =>{

            const WriteAsync = async (processedChunk)=>{
                let generator = this.F.generatorWithIndex(processedChunk.processedChunk);
                let chunkIdx  = processedChunk.chunkIdx;
                let samplePath  = [], labelPath = [];
                for(let [idx, [sample, label]] of generator){
                    let chunkSamplePath = await this.preProcessingStorage
                            .setItem(saveDir + 'sample/' + chunkIdx + '/' + idx + '/', sample);    
                    let chunkLabelPath = await this.preProcessingStorage
                            .setItem(saveDir + 'label/' + chunkIdx + '/' + idx + '/', label);
                    samplePath = [...samplePath, chunkSamplePath];
                    labelPath = [...labelPath, chunkLabelPath];
                    this.progress({preprocessing: [chunkSamplePath, chunkLabelPath]});
                }
                return this.F.zip(samplePath, labelPath);
            };

            WriteAsync(processedChunk).then((result)=>{
                if(this.savedPreprocessing){
                    this.savedPreprocessing = [...this.savedPreprocessing, ...result];
                }
                else{
                    this.savedPreprocessing = result;
                }
                callback();
            });
        };
        let duplexer = Stream.makeDuplex(WriteFn);
        let transformer = Stream.makeTransform(TransformFn);
        let stream = duplexer.pipe(transformer).pipe(duplexer);
        return stream;
    }

    async preprocessingDataset(saveDir='/preprocessing/mnist/',storeInMemory=false){
        this.preProcessingStorage = (storeInMemory)?this.memCache:this.storage;
        let stream = this.makePreprocessingStream(saveDir);
        let generator = this.F.generatorWithIndex(this.savedChunks);
        for(let [idx, [samplePath, labelPath]] of generator){
            let sampleItem = await this.storage.getItem(samplePath, true);
            let labelItem = await this.storage.getItem(labelPath, true);
            let sample = sampleItem[samplePath];
            let label = labelItem[labelPath];
            this.logger.debug({sample, label});
            stream.push({idx, sample, label});
        }
        stream.push(null);
        return new Promise((resolve, reject)=>{
            stream.on('finish', ()=>{
                resolve(this.savedPreprocessing);
            });
        });
    }
};
export default ImageDatasetPreprocessingMixins;

import { Tensor as BaseTensor } from 'causal-net.core';
import { platform } from 'causal-net.utils';
import { default as DenseMixins } from './layers/dense.mixins'; 
/**
 * This CausalNetLayer class provide common use layers
 * mixWith:
 *  [ DenseMixins ]
