import { Tensor } from 'causal-net.core';
import { Platform } from 'causal-net.utils';
import { LoggerMixins, termLogger } from 'causal-net.log';
import { StorageMixins, indexDBStorage } from 'causal-net.storage';
import { LayerMixins, causalNetLayer } from 'causal-net.layer';
import { SupervisedModelsMixins, causalNetModels } from 'causal-net.models';
import { TrainerMixins } from 'causal-net.optimizers';

import {  PipelineBaseMixins,
          PipelineRunnerMixins,
          PipelinePredictingMixins,
          PipelineTrainingMixins,
          PipelineEvaluatingMixins,
          PipelineEnsemblePredictingMixins,
          PipelineEnsembleTrainingMixins,
          PipelineEnsembleEvaluatingMixins,
          PipelineParametersMixins,
          PipelineHyperParametersMixins,
          Function } from './pipeline/index';

export default class CausalNet extends Platform.mixWith(Tensor, [
        StorageMixins,
        LoggerMixins,
        PipelineBaseMixins,
        PipelineHyperParametersMixins,
        PipelineParametersMixins,
        LayerMixins,
        SupervisedModelsMixins,
        TrainerMixins,
        PipelineRunnerMixins,
        PipelinePredictingMixins,
        PipelineTrainingMixins,
        PipelineEvaluatingMixins,
        PipelineEnsemblePredictingMixins,
        PipelineEnsembleTrainingMixins,
        PipelineEnsembleEvaluatingMixins
    ]){
    constructor(netConfig, parameters={}){
        super();
        this.F = new Function();
        this.R = this.F.CoreFunction;
        this.setBasePipelineByConfig(netConfig);
        this.setHyperParametersByConfig(netConfig);
        this.setModelByConfig(netConfig);
        this.Parameters = parameters;
        this.setTrainerByConfig(netConfig);
        this.Storage = indexDBStorage;
        this.saveModelDir = '/saveModel/';
        this.Logger = termLogger;
        this.Layer = causalNetLayer;
        this.Models = causalNetModels;
    }  
}

import {Function as BaseFunction} from 'causal-net.core';
import {Platform} from 'causal-net.utils';
import {default as PipelineFunctionMixins} from './pipelineFunction.mixins';

export default class Function extends Platform.mixWith(BaseFunction,[PipelineFunctionMixins]){
    constructor(){
        super();
    }
}

export { default as Utils } from './causalNet.utils';
export { default as Core } from './causalNet.core';
export { default as Log } from './causalNet.log';
export { default as Storage } from './causalNet.storage';
export { default as Memcache } from './causalNet.memcache';
export { default as Preprocessing } from './causalNet.preprocessing';
export { default as Representation } from './causalNet.representation';
export { default as Datasets } from './causalNet.datasets';
export { default as Layer } from './causalNet.layer';
export { default as Models } from './causalNet.models';
export { default as Optimizers } from './causalNet.optimizers';
export { default as CausalNet } from './causality';

import {Function as BaseFunction} from 'causal-net.core';
import {Platform} from 'causal-net.utils';
import {default as PipelineFunctionMixins} from './pipelineFunction.mixins';

export default class Function extends Platform.mixWith(BaseFunction,[PipelineFunctionMixins]){
    constructor(){
        super();
    }
}

export { default as PipelinePredictingMixins } from './pipelinePredicting.mixins';
export { default as PipelineTrainingMixins } from './pipelineTraining.mixins';
export { default as PipelineEvaluatingMixins } from './pipelineEvaluating.mixins';
export { default as PipelineEnsemblePredictingMixins } from './pipelineEnsemblePredicting.mixins';
export { default as PipelineEnsembleEvaluatingMixins } from './pipelineEnsembleEvaluating.mixins';
export { default as PipelineEnsembleTrainingMixins } from './pipelineEnsembleTraining.mixins';
export { default as PipelineParametersMixins } from './pipelineParameters.mixins';
export { default as PipelineHyperParametersMixins } from './pipelineHyperParameters.mixins';
export { default as PipelineBaseMixins } from './pipelineBase.mixins';
export { default as PipelineRunnerMixins } from './pipelineRunner.mixins';
export { default as Function } from './function';

const PipelineEmbeddingMixins = (PipelineClass)=> class extends PipelineClass{
    setEmbeddingByConfig(){
    }

    set Embedding(memory){
        if(!memory){
            throw Error(`expect memory, got ${memory}`);
        }
        this.memory = memory;
    }

    get Embedding(){
        if(!this.memory){
            throw Error(`memory is not set`);
        }
        return this.memory;
    }
};
export default PipelineEmbeddingMixins;

const PipelineBaseMixins = (PipelineClass)=> class extends PipelineClass{
    set BasePipeline(basePipeline){
        this.basePipeline = basePipeline;
    }
    get BasePipeline(){
        this.basePipeline;
    }
    setBasePipelineByConfig(netConfig){
        if(!netConfig.Pipeline){
            throw Error('Pipeline must be define inside netConfig');
        }
        this.basePipeline = netConfig.Pipeline;
    }
};
export default PipelineBaseMixins;

const PipelineEnsembleEvaluatingMixins = (PipelineClass)=> class extends PipelineClass{ 
    async ensembleAccuracyTest(TestSampleGeneratorFn, modelList){
        const T = this.T;
        const testSampleGenerator = TestSampleGeneratorFn();
        let testSize = 0, pass = 0;
        for await (let {idx, batchSize, data} of testSampleGenerator){
            this.SampleSize = batchSize;
            let [batchSamples, batchLabels] = data;
            let sampleTensor = T.tensor(batchSamples).reshape([batchSize, -1]).asType('float32');
            let labelTensor  = T.tensor(batchLabels).reshape([batchSize, -1]);
            let oneHotPredict = await this.ensembleOneHotPredict(modelList, sampleTensor, batchSize);
            let correctPredicts = oneHotPredict.mul(labelTensor);
            pass += correctPredicts.sum().dataSync()[0];
            testSize += batchSize;
        }
        let accuracy = pass / testSize;
        return {accuracy, pass};             
    }
};
export default PipelineEnsembleEvaluatingMixins;

const PipelineEnsemblePredictMixins = (PipelineClass)=> class extends PipelineClass{ 
    async ensembleProbFit(modelList, sampleTensor){
        const T = this.T;
        let probs = [];
        for(let model of modelList){
            await this.loadParams(model);
            let pipeOutput = this.runPipeline(sampleTensor);
            let logProb = this.Fit(pipeOutput);
            probs.push(T.exp(logProb));
        }
        let meanProb = T.concat(probs, 0);
        if(probs.length > 1){
            meanProb = meanProb.mean(0, true);
        }
        return meanProb;
    }
    async ensemblePredict(modelList, samples, numSamples=1){
        const T = this.T;
        console.log({samples});
        let sampleTensor = T.tensor(samples).reshape([numSamples, -1]).asType('float32'); 
        let meanProb = this.ensembleProbFit(modelList, sampleTensor);
        let predict = meanProb.argMax(1);
        return predict;
    }

    async ensembleOneHotPredict(modelList, sampleTensor){
        let meanProb = await this.ensembleProbFit(modelList, sampleTensor);
        let predict = meanProb.argMax(1);
        let oneHotPredict = this.T.oneHot(predict, this.NumClasses);
        return oneHotPredict;
    }
};
export default PipelineEnsemblePredictMixins;

const PipelineEnsembleTrainingMixins = (PipelineClass)=> class extends PipelineClass{ 
};
export default PipelineEnsembleTrainingMixins;

const PipelineEvaluatingMixins= (BasePipeline)=> class extends BasePipeline{
    async accuracyTest(TestSampleGeneratorFn){
        const T = this.T, F = this.F, R = this.R;
        const testSampleGenerator = TestSampleGeneratorFn();
        let testSize = 0, pass = 0;
        for await (let {idx, batchSize, data} of testSampleGenerator){
            let [batchSamples, batchLabels] = data;
            let sampleTensor = T.tensor(batchSamples).reshape([batchSize, -1]).asType('float32'); 
            let labelTensor = T.tensor(batchLabels).reshape([batchSize, -1]);
            let pipelineOutTensor = this.runPipeline(sampleTensor);
            let onehotPredict = this.OneHotPredict(pipelineOutTensor);   
            let correctPredicts = onehotPredict.mul(labelTensor);
            pass += correctPredicts.sum().dataSync()[0];
            testSize += batchSize;
        }
        let accuracy = pass / testSize;
        return {accuracy, pass};        
    }
};

export default PipelineEvaluatingMixins;

const PipelineFunctionMixins = (FunctionClass)=> class extends FunctionClass{ 
    range(len){
        return this.R.range(0, len);
    }

    isParameter(val){
        const R = this.R;
        return R.is(Array)(val) && R.all(R.is(Number), val);
    }

    getPipeline(netConfig){
        //TODO: make assertor
        return this.R.prop('Pipeline', netConfig);
    }

    getHyperParameter(netConfig){
        //TODO: make assertor
        return this.R.prop('HyperParameters', netConfig);
    }

    getTraverse(){
        return R.curry(function(keys, item){
            R.pipe.apply(null, R.map(R.prop, keys))(item);
        }); 
    }

    isTensor(object){
        return object.size !== undefined && object.shape !== undefined && object.dtype !== undefined;
    }
};

export default PipelineFunctionMixins;

const PipelineHyperParametersMixins = (PipelineClass)=> class extends PipelineClass{
    setHyperParametersByConfig(netConfig){
        this.hyperParameters = this.F.getHyperParameter(netConfig);
        this.hyperParameterAcquisition();
    }
    hyperParameterAcquisition(){
        console.log('hyperParameterAcquisition');
        const R = this.R; 
        let hyperParameters = this.hyperParameters;
        if(!hyperParameters){
            throw Error(`${hyperParameters} not hyperParameters`);
        }
        function replaceIfMatch(value, hyperParameters){
            if(R.is(String, value) && R.startsWith('$', value)){
                const key = R.slice(1, Infinity, value);
                let param = R.prop(key, hyperParameters);
                if(!param){
                    throw Error(`${key} not found in ${Object.keys(hyperParameters)}`);
                }
                return param;
            }
            return value;
        }
        const doReplaceIfMatch = R.curry(replaceIfMatch)(R.__, hyperParameters);
        function replaceHyperParameter(pipelineItem){
            if(R.is(Array, pipelineItem)){
                return pipelineItem.map(item=>replaceHyperParameter(item));
            }
            else if(R.is(Object, pipelineItem)){
                if(typeof(pipelineItem) === 'function'){//omg, js type detect!!!
                    return pipelineItem;
                }
                else{
                    let ret = {};
                    for(let key of Object.keys(pipelineItem)){
                        ret[key] = replaceHyperParameter(pipelineItem[key]);
                    }
                    return ret;
                }
            }
            else{
                return doReplaceIfMatch(pipelineItem);
            }
        }
        this.pipeline = replaceHyperParameter(this.basePipeline);
        console.log({basePipeline:this.basePipeline, pipeline: this.pipeline});
    }

    set HyperParameters(hyperParameters){
        this.hyperParameters = hyperParameters;
    }

    get HyperParameters(){
        return this.hyperParameters;
    }

    set SampleSize(numSamples){
        if(this.HyperParameters.SampleSize !== numSamples){
            this.HyperParameters.SampleSize = numSamples;
            this.hyperParameterAcquisition();
        }
    }

    get SampleSize(){
        return this.HyperParameters.SampleSize;
    }
};
export default PipelineHyperParametersMixins;

const PipelineParametersMixins = (PipelineClass)=> class extends PipelineClass{ 
    get Parameters(){
        if(!this.parameters){
            throw Error('parameter must be set');
        }
        return (async ()=>{
            const Fn = async (param)=>(Array.from(await param.data()));
            let params = await this.extractParamFromTensorDict(this.parameters, Fn);
            return params;
        })();
    }
    set Parameters(params){
        if(!this.pipeline){
            throw Error('pipeline is not defined and must be set from basePipeline');
        }
        this.parameters = this.setOrInitParams(this.pipeline, params);
    }
    async extractParamFromTensorDict(params, fn){
        const F = this.F, R = this.R;
        const Traveller = async (params, fn)=>{
            if(F.isTensor(params)){
                return await fn(params);
            }
            else{
                let kVals = R.toPairs(params);
                let res = {};
                for(let [k, val] of kVals){
                    res[k] = await Traveller(val, fn); 
                }
                return res;
            }
        };
        return Traveller(params, fn);
    };
    async parametersSummary(){
        if(!this.parameters){
            throw Error('parameter must be set');
        }
        const Fn = async (param)=>Array.from(await param.mean().data());
        return await this.extractParamFromTensorDict(this.parameters, Fn);
    }
    setOrInitParams(pipeline, netParams){
        const R = this.R, T = this.T, F = this.F;
        let pipeParams = R.fromPairs(R.filter(([k,v])=>v !== undefined,
                                R.map(R.__, pipeline)(p=>[p.Name, p.Parameters])));
        const SetOrInit = (mainValue, subVal)=>{
            let keys = R.keys(mainValue);
            let keyMainValSubVal = R.map((k)=> [k, R.prop(k, mainValue), R.propOr(null, k, subVal)], keys);
            const ValMapping = ([key, mainVal, subVal])=>{
                if(F.isParameter(mainVal)){
                    const paramShape = mainVal;
                    if(R.isNil(subVal)){
                        return [key, T.variable(T.randomNormal(paramShape))];
                    }
                    else{
                        return [key, T.variable(T.tensor(subVal).reshape(paramShape))];
                    }
                }
                else{
                    return [key, SetOrInit(mainVal, subVal)];
                }
            };
            return R.fromPairs(R.map(ValMapping, keyMainValSubVal));
        };
        return SetOrInit(pipeParams, netParams);
    }

    async getSavedParams(){
        let fileList = await this.storage.getFileList(this.saveModelDir);
        return fileList.map(fileName=>fileName.replace(this.saveModelDir,''));
    }

    async saveParams(fileName){
        const params = await this.Parameters;
        await this.storage.writeFile(this.saveModelDir + fileName, JSON.stringify(params));
        return params;
    }
    async loadParams(fileName){
        let strParams = await this.storage.readFile(this.saveModelDir+fileName);
        let params = JSON.parse(strParams);
        this.Parameters = params;
        return await this.Parameters;
    }
};

export default PipelineParametersMixins;

const PipelinePredictMixins = (PipelineClass)=> class extends PipelineClass{

    predict(samples, numSamples=1){
        const Predict = this.Predict;
        const T = this.T;
        return T.tidy(()=>{
            let sampleTensor = T.tensor(samples).reshape([numSamples, -1]).asType('float32'); 
            let pipeOutput = this.runPipeline(sampleTensor);
            return Predict(pipeOutput);
        });
    }

    onehotPredict(samples, numSamples=1){
        const OneHotPredict = this.OnehotPredict;
        const T = this.T;
        return T.tidy(()=>{
            let sampleTensor = T.tensor(samples).reshape([numSamples, -1]).asType('float32'); 
            let pipeOutput = this.runPipeline(sampleTensor);
            return OneHotPredict(pipeOutput);
        });
    }
};
export default PipelinePredictMixins;

const PipelineRunnerMixins = (PipelineClass)=> class extends PipelineClass{
    runOpFlow(value, flow, parameters){
        const R = this.R;
        const OpsRunner = R.addIndex(R.reduce)(R.__,{result: value, trace: {}}, flow);
        var {result, trace} = OpsRunner(({result, trace}, node, idx)=>{
            if(node.Parameter){
                let params = parameters[node.Parameter];
                result = result[node.Op](params, ...node.Args);
            }
            else{
                result = result[node.Op](...node.Args);    
            };
            trace[idx] = result.shape;
            return {result, trace};
        });
        return {result, trace};
    }

    runOpLayer(value, net, parameters){
        let result = net(value, parameters);
        let trace = {};
        return {result, trace};
    }
    runLayer(value, layerConfigure, layerParameters){
        const {Name, Type, Flow, Net} = layerConfigure;
        this.logger.debug({Name, Type, Flow, Net});
        if(Type === 'Tensor'){
            let {result, trace} = this.runOpFlow(value, Flow, layerParameters);
            return {[Name]: result, trace};
        }   
        else if(Type === 'Layer'){
            let {result, trace} = this.runOpLayer(value, Net, layerParameters);
            return {[Name]: result, trace};
        }   
        else{
            throw Error('type must be either Layer or Tensor');
        }
    }

    tracing(traces, name, trace){
        if(traces){
            traces.push({[name]: trace});
        }
    }
    runPipeline(samples, traces=[]){
        let pipeValue = {PipeInput: samples}, lastLayer = 'PipeInput';
        for(let layer of this.pipeline){
            let layerOutput = this.runLayer(pipeValue[lastLayer], layer, this.parameters[layer.Name]);
            pipeValue[layer.Name] = layerOutput[layer.Name];
            lastLayer = layer.Name;
            this.tracing(traces, layer.Name, layerOutput.trace);
        }
        this.logger.debug({traces});
        return pipeValue[lastLayer];
    }
};

export default PipelineRunnerMixins;

const PipelineTrainingMixins = (PipelineClass)=> class extends PipelineClass{
    trainProgress(epochIdx, losses, numEpochs){
        if(!this.startTimeStamp){
            this.startTimeStamp = new Date();
        }
        if(this.logger.progress){
            this.logger.progress({epochIdx, losses,  
                         'start at': this.startTimeStamp.toISOString(), 
                         'elapse in second': (new Date() - this.startTimeStamp)/1000});
        }
        if(epochIdx === numEpochs - 1){//done
            this.startTimeStamp = null;
        }
    }

    loss(data, numSamples){
        const Loss = this.Loss;
        this.SampleSize = numSamples;
        const T = this.T;
        let [batchSamples, batchLabels] = data;
        return T.tidy(()=>{
            let sampleTensor = T.tensor(batchSamples).reshape([numSamples, -1]).asType('float32'); 
            let labelTensor = T.tensor(batchLabels).reshape([numSamples, -1]);
            let pipelineOutTensor = this.runPipeline(sampleTensor);
            let loss = Loss(pipelineOutTensor, labelTensor);
            return loss;
        });
    };

    async train(SampleGeneratorFn, numEpochs = 2){
        const F = this.F, R = this.R;
        const Trainer = this.Trainer;
        let losses = [];
        for(let epochIdx of F.range(numEpochs)){
            const sampleGenerator = SampleGeneratorFn(epochIdx);
            let iterLosses = [];
            for await (let {idx, batchSize, data} of sampleGenerator){
                Trainer.fit(()=>{
                    let loss = this.loss(data, batchSize);
                    iterLosses.push(loss.dataSync());
                    return loss;
                });
            }
            losses.push(R.mean(iterLosses));
            iterLosses = [];
            this.trainProgress(epochIdx, losses, numEpochs);
        }
        return new Promise((resolve, reject)=>{
            resolve({losses});
        });
    };
};
export default PipelineTrainingMixins;

