export { default as causalNetModels } from './causalNetModels';
export { default as ModelMixins } from './model.mixins';

export { default as BaseModel } from './baseModel';
export { default as SingleLabelClassification } from './singleLableClassification';

const ModelMixins = (BasePipelineClass)=> class extends BasePipelineClass{
    get LossModel(){
        if(!this.netModel){
            throw Error('netModel is not set');
        }
        return this.netModel.Loss;
    }

    get FitModel(){
        if(!this.netModel){
            throw Error('netModel is not set');
        }
        return this.netModel.Fit;
    }

    get OneHotPredictModel(){
        if(!this.netModel){
            throw Error('netModel is not set');
        }
        return this.netModel.OneHotPredict;
    }

    get PredictModel(){
        if(!this.netModel){
            throw Error('netModel is not set');
        }
        return this.netModel.Predict;
    }

    get EncodeModel(){
        if(!this.netModel){
            throw Error('netModel is not set');
        }
        return this.netModel.Encode;
    }

    get DecodeModel(){
        if(!this.netModel){
            throw Error('netModel is not set');
        }
        return this.netModel.Decode;
    }


    get Model(){
        if(!this.netModel){
            throw Error('netModel is not set');
        }
        return this.netModel;
    }


    set Model(model){        
        this.netModel = model;
    }

    setByConfig(pipelineConfig){
        if(super.setByConfig){
            super.setByConfig(pipelineConfig);
        }
        this.Logger.groupBegin('set Model by config');
        const { Model } = pipelineConfig.Net;
        if(!Model){
            throw Error(`Model is not set in ${JSON.stringlify(pipelineConfig)}`);
        }
        Model.LayerRunner = this.LayerRunner;
        this.Model = Model;
        this.Logger.groupEnd();
    }
};

export default ModelMixins;

import { default as BaseModel } from './baseModel';

class SingleLabelClassification extends BaseModel{
    constructor(numClass){
        super();
        if(numClass > 0){
            this.numClass = numClass;
        }
        else{
            throw Error(`expect numclass, get ${numClass}`);
        }
    }

    set LayerRunner(layerRunner){
        let { Predictor } = layerRunner;
        this.runner = { Predictor };
    }

    get LayerRunner(){
        if(!this.runner){
            throw Error('runner is not set');
        }
        return this.runner;
    }

    get Fit(){
        const { Predictor } = this.LayerRunner;
        return (inputTensor)=>{
            let outPutTensor = Predictor(inputTensor);
            let logProb = outPutTensor.sub(outPutTensor.logSumExp(1, true));
            return logProb;
        };
    }

    get Predict(){
        const Fit = this.Fit;
        return (inputTensor)=>{
            let logProb = Fit(inputTensor);
            let predictedClass = logProb.argMax(1);
            return predictedClass;
        };
    }

    get OneHotPredict(){
        const Predict = this.Predict;
        return (inputTensor)=>{
            let predictedClass = Predict(inputTensor);
            let oneHotPredict = this.T.oneHot(predictedClass, this.numClass);
            return oneHotPredict;
        };
    }
    get Loss(){
        const Fit = this.Fit;
        return (inputTensor, labelTensor)=>{
            let logProb = Fit(inputTensor);
            let likelihood = logProb.neg().mul(labelTensor);
            let loss = likelihood.sum(1).mean();
            return loss;
        };
    }
}
export default SingleLabelClassification;

const SGDFitParamsMixins = (BaseOptimizerClass) => class extends BaseOptimizerClass{
    fit(lossFn, trainableVars){
        return this.trainer.minimize(lossFn, true, trainableVars);
    }
};

export default SGDFitParamsMixins;

import { Tensor as BaseTensor } from 'causal-net.core';
import { platform } from 'causal-net.utils';
import { default as functor } from './functor';
import { default as SGDFitParamsMixins } from './SGDFitParams.mixins';
export default class AdamOptimizer extends platform.mixWith(BaseTensor, 
    [ SGDFitParamsMixins ]){
    constructor(optimizerParams={}){
        super();
        let DefaultParameters = {beta1: 0.1, beta2: 0.2, epsilon:0.03};
        const {learningRate, beta1, beta2, epsilon} = functor.mergeParams(optimizerParams, DefaultParameters);
        this.params = { learningRate, beta1, beta2, epsilon };
        this.trainer = this.T.train.adam(learningRate, beta1, beta2, epsilon);
    }

    get Params(){
        return this.params;
    }
}

import {default as AdamOptimizer} from './adamOptimizer';
/**
 * This class provides optimizing methods which can be accessed via **causalNetSGDOptimizer** instance.
 *
 * @class CausalNetSGDOptimizer
 * @extends {Tensor}
 * @example
 * [EXAMPLE ../examples/causalNetSGDOptimizers.babel.js]
 */
class CausalNetSGDOptimizer extends platform.mixWith(BaseTensor, [SGDFitParamsMixins]){
    constructor(){
    }
    adam(optimizerParams){
        return new AdamOptimizer(optimizerParams); 
    }
}

export default new CausalNetSGDOptimizer();

import { Functor as BaseFunctor } from 'causal-net.core';
class Functor extends BaseFunctor{
    constructor(){
        super();
    }

    mergeParams(optimizerParams, defaultOptimizerParameters){
        const R = this.R;
        return R.mergeDeepLeft(optimizerParams, defaultOptimizerParameters);
    }
}

export default new Functor();

/**
 * This mixin class provides methods: **test** and 
 * handle **TestDataGenerator** setting of pipelineConfig.Dataset.
 *
 * @class EvaluatorMixins
 * @extends {BasePipelineClass}
 * @example
 * [EXAMPLE ../examples/trainer.mixins.babel.js]
 */
const EvaluatorMixins = (BasePipelineClass)=> class extends BasePipelineClass{

    get TestDataGenerator(){
        if(!this.testDataGenerator){
            throw Error('testDataGenerator is not set');
        }
        return this.testDataGenerator;
    }
    set TestDataGenerator(testDataGenerator){
        this.testDataGenerator = testDataGenerator;
    }

    async test(batchSize=32){
        const T = this.T, R = this.F.CoreFunctor;
        const TestDataGenerator = this.TestDataGenerator, OneHotPredictModel = this.OneHotPredictModel;
        return new Promise(async (resolve, reject)=>{
            const testData = TestDataGenerator(batchSize);    
            let pass = [];
            for await (let { samples, labels } of testData){
                let sampleTensor = T.tensor(samples).asType('float32');
                let labelTensor = T.tensor(labels).asType('float32');
                let predictLabelTensor = OneHotPredictModel(sampleTensor);
                let correctPredicts = predictLabelTensor.mul(labelTensor);
                pass = [...pass, ... await correctPredicts.sum(1).data()];
            }
            let accuracy = R.mean(pass);
            resolve({accuracy, pass});     
        });
    }


    setByConfig(pipelineConfig){
        if(super.setByConfig){
            super.setByConfig(pipelineConfig);
        }
        this.Logger.groupBegin('set Evaluator by config');
        this.TestDataGenerator = pipelineConfig.Dataset.TestDataGenerator;
        this.Logger.groupEnd();
    }
};

export default EvaluatorMixins;

