    async connect(){
        if(global){
            //TODO: make better with platform
            global.fetch = fetch.fetch;
        }
        this.model = await this.use.load();
        return this;
    }

    async sentenceEncode(sentences){
        if(!this.model){
            throw Error(`model is not connect`);
        }
        let embeddings = await this.model.embed(sentences);
        return embeddings;
    }
}
